Object.Field:
import oracle.odi.domain.odireference.*
import oracle.odi.domain.adapter.*
import oracle.odi.domain.adapter.topology.*
import oracle.odi.domain.adapter.relational.*
import oracle.odi.domain.adapter.project.*
import oracle.odi.domain.adapter.project.IKnowledgeModule.ProcessingType
import oracle.odi.domain.mapping.*
import oracle.odi.domain.mapping.component.*
import oracle.odi.domain.mapping.physical.*
import oracle.odi.domain.mapping.generator.*
import oracle.odi.domain.mapping.expression.*
import oracle.odi.mapping.generation.spark.*
import oracle.odi.mapping.generation.AbstractSyntaxTree.ASTProducerMethod;
import oracle.odi.domain.mapping.generator.KMGeneratorDelegateRegistry.GeneratorDelegateClass;


	@GeneratorDelegateClass(componentType=AggregateComponent.COMPONENT_TYPE_NAME, descriptionKey="GeneratorDelegateClass.SparkAggregateCompKM")
	public class XKM_Spark_Aggregate extends SparkKM {

		@ASTProducerMethod(processingType=ProcessingType.SOURCE,producedASTType=SparkScript.class, styleKey="ProcessingStyleKey.SparkAggregate.SELECT")
		SparkScript SELECT(SparkScript inputScript) {

			supportDataFrames('XKM Spark Aggregate', true);

			String windowing = getOptionValue("WINDOW_AGGREGATION")

			if (windowing != null && windowing != "") {
			  if (!inputScript.isCheckpointEnabled() && Boolean.valueOf(windowing)) {
			    throw new OdiKMException("ODIKM-SPARK-AGGR-10000 : Checkpointing must be enabled when WINDOW_AGGREGATION = true. Set Spark execution unit EKM option spark.checkpointing = True to enable checkpointing.")
			  }
			}

			String stateful = getOptionValue("STATEFUL_AGGREGATION")

			if (stateful != null && stateful != "") {
			  if (!inputScript.isCheckpointEnabled() && Boolean.valueOf(stateful)) {
			    throw new OdiKMException("ODIKM-SPARK-AGGR-10001 : Checkpointing must be enabled when STATEFUL_AGGREGATION = true. Set Spark execution unit EKM option spark.checkpointing = True to enable checkpointing.")
			  }
			}

			SparkGroupCmd groupCmd = new SparkGroupCmd(physicalNode.getTemplateUtils(), physicalNode);
			groupCmd.setAllAggFunctions(physicalNode.getExecutionUnit().getExecutionTechnology().getAggregateFunctionList());

			AggregateComponent aggrComp = (AggregateComponent)component;

			List<MapExpression> groupByExprs = new ArrayList<MapExpression>();
			List<String> groupByAliases = new ArrayList<String>();
			List<MapExpression> directMapExprs = new ArrayList<MapExpression>();
			List<String> directMapAliases = new ArrayList<String>();
			List<MapExpression> simpleAggrExprs = new ArrayList<MapExpression>();
			List<String> simpleAggrFuncs = new ArrayList<String>();
			List<String> simpleAggrAliases = new ArrayList<String>();
			List<MapExpression> complexAggrExprs = new ArrayList<MapExpression>();
			List<String> complexAggrAliases = new ArrayList<String>();
			List<MapExpression> allAggrExprs = new ArrayList<MapExpression>();
			List<String> allAggrAliases = new ArrayList<String>();

			int groupby = 0;
			int simpleAggr = 0;
			int complexAggr = 0;
			int directMap = 0;
			int allAggr = 0;

			for (MapAttribute attr : aggrComp.getAttributes()) {
				boolean isGroupFunction = false;
				String groupByOption = attr.isGroupFunction().toUpperCase();
				// if the option is AUTO, we try to figure out whether the attribute is in the group by clause, else use the user set yes or no value an attribute must be in the group by if it does NOT participate in an aggregation.
				if (groupByOption.equals("AUTO")) {
					def containsAggregate = attr.getExpression().containsAggregationFunction(physicalNode.getExecutionUnit().getExecutionTechnology())
					if (!containsAggregate) {
					  // check if lower case aggregate function has been used by error
					  def clonedExpr = attr.getExpression().clone()
					  clonedExpr.setText(clonedExpr.getText().toUpperCase())
					  if (clonedExpr.containsAggregationFunction(physicalNode.getExecutionUnit().getExecutionTechnology()))  {
					    if (isDataFramesEnabled ()) {
						isGroupFunction = false
					    } else {
						throw new OdiKMException("ERROR : Attribute expression " + attr.getExpression().getText() + " contains lower case aggregate function call. Aggregate functions must be in upper case.")
					    }
					  } else {
						isGroupFunction = true
					  }
					} else {
                                  isGroupFunction = false
					}

				} else if (groupByOption.equals("YES")) {
					isGroupFunction = true;
				}

				if (isGroupFunction) { //group by
					if (attr.getDataType() != null && attr.getDataType().getName().toUpperCase().equals("COMPLEX")) {
					  throw new OdiKMException("ERROR : Complex type attribute " + attr.getName() + " cannot be part of Group By clause, set Group By to No.")
					}
					groupByExprs.add(attr.getExpression());
					groupByAliases.add(attr.getName());
					groupby++;
				} else {
					if (attr.getExpression().containsAggregationFunction(physicalNode.getExecutionUnit().getExecutionTechnology())) {

						String exprText = attr.getExpression().getText();
						//Simple expression
						if (attr.getExpression().getFunctionCallList().size() == 1 && exprText.matches(/^[a-zA-Z]+\(([^)]*)\)$/)) {							
							simpleAggrExprs.add(new MapExpression(attr, exprText.substring(exprText.indexOf("(") + 1, exprText.length()-1)));
							simpleAggrFuncs.add(exprText.substring(0, exprText.indexOf("(")));
							simpleAggrAliases.add(attr.getName());
							simpleAggr++;
						} else {
							complexAggrExprs.add(attr.getExpression());
							complexAggrAliases.add(attr.getName());
							complexAggr++;
						}
					} else { 
						// check if lower case aggregate function has been used by error
						def clonedExpr = attr.getExpression().clone()                                              
						clonedExpr.setText(clonedExpr.getText().toUpperCase())
						if (clonedExpr.containsAggregationFunction(physicalNode.getExecutionUnit().getExecutionTechnology()) && !isDataFramesEnabled ())  {
						    throw new OdiKMException("ERROR : Attribute expression " + attr.getExpression().getText() + " contains lower case aggregate function call. Aggregate functions must be in upper case.")
						} else {
						  // direct map
						  directMapExprs.add(attr.getExpression());
						  directMapAliases.add(attr.getName());
						  directMap++;
						}
					}					
				}
				allAggrExprs.add(attr.getExpression());
				allAggrAliases.add(attr.getName());
				allAggr++;
			}

			if (groupby > 0) {
				groupCmd.setGroupByExprs(groupByExprs);
				groupCmd.setGroupByAliases(groupByAliases);
			} else {
				groupCmd.setGroupByExprs(null);
				groupCmd.setGroupByAliases(null);
			}
			if (simpleAggr > 0) {
				groupCmd.setSimpleAggrExprs(simpleAggrExprs);
				groupCmd.setSimpleAggrFuncs(simpleAggrFuncs);
				groupCmd.setSimpleAggrAliases(simpleAggrAliases);
			} else {
				groupCmd.setSimpleAggrExprs(null);
				groupCmd.setSimpleAggrFuncs(null);
				groupCmd.setSimpleAggrAliases(null);
			}

			if (complexAggr > 0) {
				groupCmd.setComplexAggrExprs(complexAggrExprs);
				groupCmd.setComplexAggrAliases(complexAggrAliases);
			} else {
				groupCmd.setComplexAggrExprs(null);
				groupCmd.setComplexAggrAliases(null);
			}

			if (allAggr > 0) {
				groupCmd.setAllAggrExprs(allAggrExprs);
				groupCmd.setAllAggrAliases(allAggrAliases);
			} else {
				groupCmd.setAllAggrExprs(null);
				groupCmd.setAllAggrAliases(null);
			}

			if (directMap > 0) {
				groupCmd.setDirectMapExprs(directMapExprs);
				groupCmd.setDirectMapAliases(directMapAliases);
			} else {
				groupCmd.setDirectMapExprs(null);
				groupCmd.setDirectMapAliases(null);
			}

			//having condition
			String havingCond = aggrComp.getHavingText();
			if (havingCond == null || havingCond.length() == 0) {
				groupCmd.setHavingCond(null);
			} else {
				groupCmd.setHavingCond(aggrComp.getHaving());
			}

			//manual group by clause
			MapExpression manualGroupBy = aggrComp.getManualGroupBy();
			groupCmd.setManualGroupBy(manualGroupBy);

			groupCmd.setUseSQLExpressions(getOptionValue("SQL_EXPRESSIONS").equalsIgnoreCase("true") ? true : false)

			// Processes WINDOW_AGGREGATION, WINDOWING, WINDOW_LENGTH, and SLIDING_INTERVAL options
			groupCmd.setWindowingValues(groupCmd);

			//set STATEFUL_AGGREGATION option
			def statefulOption = physicalNode.getOptionValue(oracle.odi.domain.adapter.project.IKnowledgeModule.ProcessingType.SOURCE, "STATEFUL_AGGREGATION");
			if (statefulOption != null) {
			  def statefulOptionValue = statefulOption.getOptionValue().toString();
			  if (statefulOptionValue.length() > 0)
				groupCmd.enableStateful(Boolean.valueOf(statefulOptionValue));
			}

			//set FORWARD_ONLY_UPDATED_ROWS option
			def updatesOnlyOption = physicalNode.getOptionValue(oracle.odi.domain.adapter.project.IKnowledgeModule.ProcessingType.SOURCE, "FORWARD_ONLY_UPDATED_ROWS");
			if (updatesOnlyOption != null) {
			  def updatesOnlyOptionValue = updatesOnlyOption.getOptionValue().toString();
			  if (updatesOnlyOptionValue.length() > 0)
				groupCmd.enableUpdatesOnly(Boolean.valueOf(updatesOnlyOptionValue));
			}

			//set STATE_RETENTION_PERIOD option
			def retentionOption = physicalNode.getOptionValue(oracle.odi.domain.adapter.project.IKnowledgeModule.ProcessingType.SOURCE, "STATE_RETENTION_PERIOD");
			if (retentionOption != null) {
			  def retentionOptionValue = retentionOption.getOptionValue().toString();
			  if (retentionOptionValue.length() > 0) {
				groupCmd.setStateRetention(Integer.valueOf(retentionOptionValue));
			  } else {
  				groupCmd.setStateRetention(0);
			  } 
			}

			List<SparkScript> scripts = new ArrayList<SparkScript>(1);
			scripts.add(inputScript); 

			SparkScript groupScript = new SparkScript(inputScript.getTemplateUtils(), scripts);
			groupScript.addChild(groupCmd);

			return groupScript;
		}
	}
